"use client";

import { fetchPostInfinite } from "@/lib/axios/action";
import { Button, Card, Col, Empty, Row } from "antd";
import Link from "next/link";
import { BsCurrencyYen } from "react-icons/bs";
import { CiClock1 } from "react-icons/ci";
import { FaCheck } from "react-icons/fa";
import { GrLanguage } from "react-icons/gr";
import { MdOutlineVerified } from "react-icons/md";
import { TfiLocationPin } from "react-icons/tfi";
import useSWRInfinite from "swr/infinite";
import { motion } from "framer-motion";
import InfiniteScroll from "@/utils/InfiniteScroll";
import { fadeIn } from "@/utils/FramerScrollAnimate";
import Image from "next/image";
import { useParams, usePathname, useSearchParams } from "next/navigation";
import { Data } from "@/types/type";
import { formatDistanceToNowStrict } from "date-fns";
import { id } from "date-fns/locale";

export default function CardJob({
  title,
  bahasa,
  category,
}: {
  title?: string;
  bahasa?: string;
  category?: string;
}) {
  const PAGE_LIMIT = 9;
  const pathname = usePathname();

  const swr = useSWRInfinite(
    (index, prev) => {
      if (pathname == "/") {
        return `api/posts?pagination[limit]=${PAGE_LIMIT}&pagination[start]=${
          index * PAGE_LIMIT
        }&sort[0]=createdAt:desc&&populate=deep`;
      }
      if (pathname == "/engineer") {
        return `api/posts?filters[category][$eqi]=engineer&pagination[limit]=${PAGE_LIMIT}&pagination[start]=${
          index * PAGE_LIMIT
        }&sort[0]=createdAt:desc&&populate=deep`;
      }
      if (pathname == "/tokutei-ginou") {
        return `api/posts?filters[category][$eqi]=tokutei-ginou&pagination[limit]=${PAGE_LIMIT}&pagination[start]=${
          index * PAGE_LIMIT
        }&sort[0]=createdAt:desc&&populate=deep`;
      }
      if (pathname.includes("/search")) {
        return `api/posts?filters[title][$containsi]=${title}&filters[bahasa][$containsi]=${bahasa}&filters[category][$containsi]=${category}&pagination[limit]=${PAGE_LIMIT}&pagination[start]=${
          index * PAGE_LIMIT
        }&sort[0]=createdAt:desc&&populate=deep`;
      }
    },
    {
      fetcher: async (url) => await fetchPostInfinite(url),
    }
  );

  return (
    <div className="  space-y-5 ">
      <InfiniteScroll
        swr={swr}
        loadingIndicator={
          <div className=" grid grid-cols-1 xl:grid-cols-3 gap-5">
            {[1, 2, 3].map(() => (
              <Card
                key={1}
                loading={true}
                className="w-[350px] md:w-[700px] xl:w-96 "
                bordered={false}
              />
            ))}
          </div>
        }
        // endingIndicator={
        //   <div className="w-full  mt-10 border-t-2 border border-pink-400 rounded-full"></div>
        // }
        isReachingEnd={(swr) => {
          return (
            swr.data?.[0]?.length === 0 ||
            swr.data?.[swr.data?.length - 1]?.length < PAGE_LIMIT
          );
        }}
      >
        {(response) => (
          <motion.div
            variants={fadeIn("up", 0.2)}
            initial="hidden"
            whileInView={"show"}
            transition={{ duration: 0.5 }}
            className=" grid grid-cols-1 xl:grid-cols-3 gap-5 w-full"
          >
            {response <= 0 && (
              <div className=" z-20 shadow-md w-full flex bg-slate-50  p-10 rounded-md justify-center items-center">
                <Empty />
              </div>
            )}
            {response?.map((item: Data) => {
              // let image = "/default2.jpg";
              // fallback="data:image/png;base64,"
              let image =
                "data:image/png;base64,";
              if (item.gambar && item.gambar !== null) {
                if (item.gambar[0].formats.large) {
                  image = `${process.env.NEXT_PUBLIC_URL_BACKEND}${item.gambar[0]?.formats?.large?.url}`;
                } else {
                  image = `${process.env.NEXT_PUBLIC_URL_BACKEND}${item?.gambar[0]?.formats?.small?.url}`;
                }
              }

              const uploadTime = new Date(item.createdAt);

              // Hitung jarak waktu antara sekarang dengan waktu upload
              const timeAgo = formatDistanceToNowStrict(uploadTime, {
                locale: id,
              });

              return (
                <Link
                  key={item.id}
                  // href={`/${item.category.toLowerCase()}/${item.title
                  //   .toLowerCase()
                  //   .replaceAll(" ", "-")
                  //   .replaceAll(",", "")
                  //   .replaceAll(".", "-")}`}
                  href={`/${item.category.toLowerCase()}/${item.documentId}`}
                >
                  <Card
                    hoverable
                    className="w-[350px] md:w-[700px] xl:w-96 "
                    title={item.title}
                    bordered={false}
                  >
                    <div className="flex text-xs pb-2 gap-2 items-center">
                      <span>{timeAgo} lalu</span>
                      <CiClock1 />
                    </div>
                    <div className="w-full h-48 relative bg-red-300">
                      <Image
                        // src={"/default2.jpg"}
                        src={image}
                        alt={"jobs-image"}
                        fill
                        quality={75}
                        className="object-cover"
                      />
                      {/* {!item.gambar && (
                        <span className="absolute text-white  font-semibold text-2xl flex items-center w-full h-full justify-center">
                          No Image
                        </span>
                      )} */}
                    </div>
                    <div className="py-2 flex flex-col gap-2">
                      <div className="w-full flex justify-between">
                        <div className="flex text-xs gap-2 items-center">
                          <TfiLocationPin />
                          <span>{item.lokasi}</span>
                        </div>
                      </div>
                      {/*  */}
                      <div className="w-full flex justify-between">
                        <div className="flex text-xs gap-2 items-center">
                          <BsCurrencyYen />
                          <span>{item.gaji}</span>
                        </div>
                        {item.bonus && (
                          <div className="flex text-xs gap-2 items-center">
                            <span>Bonus</span>
                            <FaCheck />
                          </div>
                        )}
                      </div>
                      {/*  */}
                      <div className="w-full flex justify-between">
                        <div className="flex text-xs gap-2 items-center">
                          <MdOutlineVerified />
                          <span>{item.category}</span>
                        </div>
                        <div className="flex text-xs gap-2 items-center">
                          <span>{item.bahasa}</span>
                          <GrLanguage />
                        </div>
                      </div>
                    </div>
                  </Card>
                </Link>
              );
            })}
          </motion.div>
        )}
      </InfiniteScroll>
    </div>
  );
}
